import clsx from 'clsx'
import React, { useState } from 'react'
import Modal from '../Modal/Modal'
import Card from './Card/Card'
import Styles from './MyStake.module.css'


const MyStake = ({
  withdrawInput, 
  stakeInput, 
  addressInput,
  onClickAddress,
  onChangeInput, 
  onClickStake, 
  stakeDetails,
  onClickWithdraw,
  rewardAmount,
  stakeAmount,
  connected
}) => {
  const [show, setShow] = useState(false)

  const showModalHandler = () => {
    setShow(!show)
  }
  const {amount} = stakeDetails;
  console.log(amount);
  return (
    <div className={Styles.root}>
        <h2 className={Styles.heading}>My stake</h2>

        <div className={Styles.stake_body}>
          <div className={Styles.card_container}>
            <Card 
              cardKey="Total Staked"
              cardValue = {stakeAmount}
            />
            <Card 
              cardKey="Total Reward"
              cardValue = {rewardAmount}
            />
          </div>
          <div>
          <form onSubmit={onClickStake} className={Styles.form} >
            <input 
              type = "number" 
              placeholder="Amount to stake" 
              className={Styles.input}
              value = {stakeInput}
              onChange = {onChangeInput}
              id = "stake"
            />
            <button type='submit' className={clsx({[Styles.stake_btn]: true, [Styles.btn_diabled]: !connected})}
              disabled = {!connected}
            >Stake</button>
          </form>

          <form onSubmit = {onClickWithdraw} className={Styles.form} >
            <input 
              type = "number" 
              placeholder="Amount to unstake" 
              className={Styles.input}
              value = {withdrawInput}
              onChange = {onChangeInput}
              id = "unstake"
            />
            <button type="submit"
            className={clsx({[Styles.unstake_btn]: true, [Styles.btn_diabled]: !connected})}
            disabled = {!connected}
            >Unstake</button>
          </form>
          <form onSubmit = {onClickAddress} className={Styles.form} >
            <input 
              type = "text" 
              placeholder="check details" 
              className={Styles.input}
              value = {addressInput}
              onChange = {onChangeInput}
              id ="address"
            />
            <button type="submit"
            className={clsx({[Styles.stake_btn]: true, })}
            >Details</button>
          </form>
          </div>
          <h5>YOur stake details are:</h5>
          <h5>YOur stake details are:</h5>
          <h5>YOur stake details are:</h5>
        </div>
        {/* <button onClick={showModalHandler}>Click me</button> */}
        {/* {
          show &&
          <Modal show={show} close={showModalHandler}>
          <h1>Hello world</h1>
          <h1>Hello world</h1>
        </Modal>

        }
         */}
        {/* {amount} */}
        {/* <h2>Your stake: {stakeDetails? `${stakeDetails} BRT`: "00.00" }</h2> */}

    </div>
  )
}

export default MyStake
