import './App.css';
import Header from './components/header/Header';
import MyStake from './components/MyStake/MyStake';
import StakeHistory from './components/StakeHistory/StakeHistory';
import {useEffect, useState} from 'react'
import Footer from './components/Footer/Footer';
import { ethers } from 'ethers';

function App() {

  const [connected, setConnected] = useState(false);

  const [userInfo, setUserInfo] = useState({
    matic_balance: 0,
    token_balance: 0,
    address: null
  });

  const [stakeAmount, setStakeAmount] = useState(null)
  const [rewardAmount, setRewardAmount] = useState(null)

  const [stakeInput, setStakeInput] = useState("");
  const [withdrawInput, setWithdrawInput] = useState("");

  const [stateHistory, setStakeHistory] = useState([
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "stake"
    },
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "stake"
    },
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "unstake"
    },
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "stake"
    },
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "unstake"
    },
    {
      amount: 1000,
      account: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415",
      time: "1647975426",
      type: "stake"
    }
  ])

  useEffect(() =>{
    // append handlers
    window.ethereum.on("connect", payload =>{
      if(Number(payload.chainId) !== 80001) return alert( "you are on the wrong network, please chnage to mumbia")

    // you are on the right network
    const provider =  new ethers.providers.Web3Provider(window.ethereum)
    console.log(provider.listAccounts());
    })
  },[])
// To  connect wallet
  const connectWallet = async () => {
    // logic
    // check if window.ethereum is present by running the method eth_requestAccounts: this makes metamask pop up
    if(!!window.ethereum || !!window.web3){
await window.ethereum.request({method:"eth_requestAccounts"})
// ensure the user is on the right network

    }

    // setConnected(true)
    // setUserInfo(
    //   {
    //     matic_balance: "63549678582439050349",
    //     token_balance: "65045396805965968546",
    //     address: "0xE428Db9A3B47046acb020B8B5a5B29b8792a1415"
    //   }
    // )
  }

  const onChangeInput = ({target}) => {
    switch (target.id) {
      case "stake":
        setStakeInput(target.value)
        break;

      case "unstake":
        setWithdrawInput(target.value);
        break;

      default:
        break;
    }
  }

  const onClickStake = (e) => {
    e.preventDefault()
    console.log("staking...........", stakeInput);
  }

  const onClickWithdraw = (e) => {
    e.preventDefault()
    console.log("unstaking...........", withdrawInput);
  }
  // 0x169E82570feAc981780F3C48Ee9f05CED1328e1b

  return (
    <div className="App">
      <Header 
        connectWallet = {connectWallet}
        connected={connected}
        userInfo = {userInfo}
      />
      <main className='main'>
        <MyStake
          stakeInput = {stakeInput}
          withdrawInput = {withdrawInput}
          onChangeInput = {onChangeInput}
          onClickStake = {onClickStake}
          onClickWithdraw = {onClickWithdraw}
          stakeAmount = {stakeAmount}
          rewardAmount = {rewardAmount}

        />
        <StakeHistory
          stakeData = {stateHistory}
        />
      </main>
      <Footer />
    </div>
  );
}

export default App;
